        flex-direction: column;
        align-items: flex-start;
        gap: 1rem;
    }

    .nav-menu li {
        width: 100%;
    }

    .nav-menu li a {
        display: block;
        padding: 1rem;
        width: 100%;
    }

    /* Hero */
    .hero-section {
        padding: 5rem 0;
    }

    /* About Page */
    .about-content {
        grid-template-columns: 1fr;
        gap: 2rem;
    }

    /* Contact Page */
    .contact-wrapper {
        grid-template-columns: 1fr;
        gap: 2rem;
    }

    /* Footer */
    .footer-content {
        grid-template-columns: 1fr;
        gap: 2rem;
    }
}

@media (max-width: 480px) {
    .services-grid,
    .team-grid {
        grid-template-columns: 1fr;
    }

    h1 {
        font-size: 2rem;
    }

    .hero-content h1 {
        font-size: 2rem;
    }
}

